import {React, useState} from "react";
import "../styles/ProductList.css";

const ProductList = ({ products, onView, onEdit, onDelete, onAdd }) => {
    // const [setProducts] = useState(false);
    const [showForm, setShowForm] = useState(false);
    const [newProduct, setNewProduct] = useState({
        name: "",
        cost_price: "",
        sellig_price: "",
        price: "",
        quantity: "",
        category: "",
    });
      
    const handleAddProduct = async (e) => {
    e.preventDefault();
    try {
        const response = await fetch("http://localhost:5000/api/products", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newProduct),
        });
    
        const data = await response.json();
        if (response.ok) {
        setProducts((prev) => [...prev, data]); // Add new product to the list
        setNewProduct({ name: "", price: "", quantity: "", category: "" }); // clear form
        setShowForm(false); // hide form
        } else {
        console.error("Error:", data.message);
        }
    } catch (error) {
        console.error("Network error:", error);
    }
    };
    

    return (
    <div className="product-list-container">
      <div className="product-list-header">
        <h2>Product List</h2>
        <button className="add-btn" onClick={() => setShowForm(!showForm)}>+ Add Product</button>
      </div>
      {showForm && (
            <form onSubmit={handleAddProduct}>
                <input
                type="text"
                placeholder="Product Name"
                value={newProduct.name}
                onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
                required
                />
                <input
                type="number"
                placeholder="Price"
                value={newProduct.price}
                onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })}
                required
                />
                <input
                type="number"
                placeholder="Quantity"
                value={newProduct.quantity}
                onChange={(e) =>
                    setNewProduct({ ...newProduct, quantity: e.target.value })
                }
                required
                />
                <input
                type="text"
                placeholder="Category"
                value={newProduct.category}
                onChange={(e) =>
                    setNewProduct({ ...newProduct, category: e.target.value })
                }
                required
                />
                <button type="submit">Submit</button>
            </form>
            )}
      <table className="product-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Category</th>
            <th>Quantity</th>
            <th>Price</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {products && products.length > 0 ? (
            products.map((product, index) => (
              <tr key={index}>
                <td>{product.name}</td>
                <td>{product.category}</td>
                <td>{product.quantity}</td>
                <td>${product.price}</td>
                <td className="actions">
                  <button className="view-btn" onClick={() => onView(product)}>View</button>
                  <button className="edit-btn" onClick={() => onEdit(product)}>Edit</button>
                  <button className="delete-btn" onClick={() => onDelete(product._id)}>Delete</button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="5" className="no-data">No products found.</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default ProductList;
