import {React, useState} from "react";
import "../styles/ProductList.css";

const ProductList = ({ products, onView, onEdit, onDelete, onAdd }) => {
    // const [product , setProducts] = useState();
    const [showForm, setShowForm] = useState(false);
    // const [formProduct, setformProduct] = useState({
    //   name: "",
    //   cost_price: "",
    //   selling_price: "",
    //   quantity: "",
    //   category: "",
    // });

    const [formProduct, setFormProduct] = useState({
      name: "",
      cost_price: "",
      selling_price: "",
      quantity: "",
      category: "",
    });
    const [editId, setEditId] = useState(null);
      
    const handleAddProduct = async (e) => {
    e.preventDefault();
    try {
        const response = await fetch("http://localhost:5000/api/products", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formProduct),
        });
    
        const data = await response.json();
        if (response.ok) {
        // setProducts((prev) => [...prev, data]); // Add new product to the list
        onAdd(data);
        setformProduct({ name: "",  cost_price: "", selling_price: "", quantity: "", category: "" }); // clear form
        setShowForm(false); // hide form
        } else {
        console.error("Error:", data.message);
        }
    } catch (error) {
        console.error("Network error:", error);
    }
    };

    const handleEditSubmit = async (e) => {
      e.preventDefault();
      try {
        const res = await fetch(`http://localhost:5000/api/products/${editId}`, {
          method: "PUT",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(formProduct),
        });
    
        const updated = await res.json();
    
        if (res.ok) {
          onEdit(updated); // Youâ€™ll pass this function from App.js
          setEditId(null);
          setFormProduct({
            name: "",
            cost_price: "",
            selling_price: "",
            quantity: "",
            category: "",
          });
        } else {
          console.error("Failed to update");
        }
      } catch (err) {
        console.error("Error updating:", err);
      }
    };

    return (
    <div className="product-list-container">
      <div className="product-list-header">
        <h2>Product List</h2>
        <button className="add-btn" onClick={() => setShowForm(!showForm)}>+ Add Product</button>
      </div>
      {showForm && (
            <form onSubmit={handleAddProduct}>
                <input
                type="text"
                placeholder="Product Name"
                value={formProduct.name}
                onChange={(e) => setformProduct({ ...formProduct, name: e.target.value })}
                required
                />
                <input
                type="number"
                placeholder="Cost Price"
                value={formProduct.cost_price}
                onChange={(e) => setformProduct({ ...formProduct, cost_price: e.target.value })}
                required
                />
                <input
                type="number"
                placeholder="Selling Price"
                value={formProduct.selling_price}
                onChange={(e) => setformProduct({ ...formProduct, selling_price: e.target.value })}
                required
                />
                <input
                type="number"
                placeholder="Quantity"
                value={formProduct.quantity}
                onChange={(e) =>
                    setformProduct({ ...formProduct, quantity: e.target.value })
                }
                required
                />
                <input
                type="text"
                placeholder="Category"
                value={formProduct.category}
                onChange={(e) =>
                    setformProduct({ ...formProduct, category: e.target.value })
                }
                required
                />
                <button type="submit">Submit</button>
            </form>
            )}
      <table className="product-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Category</th>
            <th>Quantity</th>
            <th>Price</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {/* {products && products.length > 0 ? (
            products.map((product, index) => (
              <tr key={index}>
                <td>{product.name}</td>
                <td>{product.category}</td>
                <td>{product.quantity}</td>
                <td>Rs{product.selling_price}</td>
                <td className="actions">
                  <button className="view-btn" onClick={() => onView(product)}>View</button>
                  <button className="edit-btn" onClick={() => onEdit(product)}>Edit</button>
                  <button className="delete-btn" onClick={() => onDelete(product._id)}>Delete</button>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="5" className="no-data">No products found.</td>
            </tr>
          )} */}


          {products && products.length > 0 ? (
            products.map((product, index) =>
              editId === product._id ? (
                <tr key={index}>
                  <td>
                    <input
                      value={formProduct.name}
                      onChange={(e) =>
                        setFormProduct({ ...formProduct, name: e.target.value })
                      }
                    />
                  </td>
                  <td>
                    <input
                      value={formProduct.category}
                      onChange={(e) =>
                        setFormProduct({ ...formProduct, category: e.target.value })
                      }
                    />
                  </td>
                  <td>
                    <input
                      type="number"
                      value={formProduct.quantity}
                      onChange={(e) =>
                        setFormProduct({ ...formProduct, quantity: e.target.value })
                      }
                    />
                  </td>
                  <td>
                    <input
                      type="number"
                      value={formProduct.selling_price}
                      onChange={(e) =>
                        setFormProduct({ ...formProduct, selling_price: e.target.value })
                      }
                    />
                  </td>
                  <td className="actions">
                    <button onClick={handleEditSubmit}>Save</button>
                    <button onClick={() => setEditId(null)}>Cancel</button>
                  </td>
                </tr>
              ) : (
                <tr key={index}>
                  <td>{product.name}</td>
                  <td>{product.category}</td>
                  <td>{product.quantity}</td>
                  <td>Rs{product.selling_price}</td>
                  <td className="actions">
                    <button className="view-btn" onClick={() => onView(product)}>View</button>
                    <button
                      className="edit-btn"
                      onClick={() => {
                        setEditId(product._id);
                        setFormProduct(product);
                      }}
                    >
                      Edit
                    </button>
                    <button className="delete-btn" onClick={() => onDelete(product._id)}>Delete</button>
                  </td>
                </tr>
              )
            )
          ) : (
            <tr>
              <td colSpan="5" className="no-data">No products found.</td>
            </tr>
          )}


        </tbody>
      </table>
    </div>
  );
};

export default ProductList;
